import React from 'react';
import Nav  from './Nav'

const url = `http://127.0.0.1:5000/user/`

export default class UserPage extends React.Component {
    constructor(props){
    super(props)
    this.state = {
        allPosts: [],
    }
}

    fetchAll = (username) => {
        fetch(url+`${username}`)
        .then (blob => blob.json()).then(json => {
            let allPosts = json
            this.setState({allPosts : allPosts})
            })
    }

    componentDidMount() {
        this.fetchAll()
    }

    pay(id) {
        console.log("pay function fired with this id::", id)
        fetch (url+`bills/${id}`)
        .then (blob => blob.json()).then(json => {
            let bill = json
            this.setState({allPosts : [bill], payForm : true, id : id})
            console.log("this is BILL in pay(id)", this.state.allPosts)
        })
    }


    render () {
        return (
            <div>
                <div>
                <Nav/>
                </div>
                <br/>
                <br/>
                <br/>
                <br/>
                <div>{this.state.allPosts}</div>
                <br/>
                <br/>
                <br/>
                <br/>
            </div>
        )
    }
}
