import React from 'react';
import Nav  from './Nav'

const url = `http://127.0.0.1:5000/user/`

export default class UserPage extends React.Component {
    constructor(props){
    super(props)
    this.state = {
        allPosts: [],
    }
}

    fetchAll = (username) => {
        fetch(url+`${username}`)
        .then (blob => blob.json()).then(json => {
            let allPosts = json
            this.setState({allPosts : allPosts})
            })
    }

    componentDidMount() {
        this.fetchAll(username)
    }


    render () {
        return (
            <div>
                <div>
                <Nav/>
                </div>
                <br/>
                <br/>
                <br/>
                <br/>
                <div>{this.state.allPosts}</div>
                <br/>
                <br/>
                <br/>
                <br/>
            </div>
        )
    }
}
